mapper.select:
SELECT count(*) FROM UserTb
WHERE id = #{id }
	AND password = #{password }

SELECT count(*) FROM PhoneAuth
WHERE phone = #{phone }
AND authKey = #{authKey }

SELECT count(*) FROM UserTb
WHERE id = #{id }
AND email = #{email }

SELECT count(*) FROM UserTb
WHERE id = #{id }
AND phone = #{phone }

mapper.update:
update UserTb
set password = #{password } 
where id = #{id }
