mapper.select:
SELECT * FROM (
	SELECT rownum rnum, R.* FROM (


		ORDER BY review_no DESC
	) R
) R
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE review
SET review_views = review_views + 1
WHERE review_no = #{review_no }

mapper.select:
SELECT review_no, user_no, nick, review_title, review_content, review_date, review_views
FROM review
WHERE review_no = #{review_no }

SELECT file_no, review_no, origin_name, stored_name
FROM review_file
WHERE review_no = #{review_no}

mapper.update:
UPDATE review
SET review_title = #{review_title }
	, review_content = #{review_content }
WHERE review_no = #{review_no }
